package com.blogging.java;

public class DoWhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 String s = "Hello world!";
	      int i =0;
	     do{
	          System.out.println(s);
	          i++;
	      } while(i<5);
	      
	    
	}

}
